import React, { useState, useEffect} from 'react';
import useMediaQuery from '@mui/material/useMediaQuery';
import {useSpring, animated} from '@react-spring/web';
import Navbar from '../components/Navbar';
import AnimatedDiv from '../components/AnimatedDiv';

const Home = () => {
  const smallScreen = useMediaQuery('(max-width: 650px)');
  const largeScreen = useMediaQuery('(min-width: 651px)');

  const [firstTimeOpened, setFirstTimeOpened] = useState(true);

  useEffect(() => {
    // Check if it's the first time the user opens the page
    const isFirstTime = localStorage.getItem('firstTimeOpened') === null;

    if (isFirstTime) {
      // If it's the first time, set a flag in localStorage
      localStorage.setItem('firstTimeOpened', 'true');
    } else {
      // If not the first time, update state to reflect that
      setFirstTimeOpened(false);
    }

    // Clear localStorage when the component unmounts (page closes)
    return () => {
      localStorage.clear();
    };
  }, []);

  // Add unload event listener to clear localStorage when the user leaves the site
  useEffect(() => {
    const clearLocalStorageOnUnload = () => {
      localStorage.clear();
    };

    window.addEventListener('unload', clearLocalStorageOnUnload);

    return () => {
      window.removeEventListener('unload', clearLocalStorageOnUnload);
    };
  }, []);

  return (
    <>
      <header style={{ margin: '0', width: '100%', top: '0', left: '0', position: 'absolute' }}>
        <Navbar />
      </header>
      {smallScreen && (
        <>
          <div className="title">
            <div className="blur-container"></div>
            <div className="title-content">
              <h1 class="centered">Welcome</h1>
              <p>This is v3 of my portfolio website. Please take a look around!</p>
            </div>
          </div>
        </>
      )}
      {largeScreen && (
        // <>
        //   <div className="blur-container centered-title title-padding-desktop">
        //     <h2>Tao Tao</h2>
        //   </div>
        // </>
        <>
          <div className="title">
            {firstTimeOpened ? (
            <AnimatedDiv>
              <div className="blur-container"></div>
              <div className="title-content">
                <h1 class="centered">Welcome</h1>
                <p>This is v3 of my portfolio website. Please take a look around!</p>
              </div>
            </AnimatedDiv>
            ) : (
              <>
              <div className="blur-container"></div>
              <div className="title-content">
                <h1 class="centered">Welcome</h1>
                <p>This is v3 of my portfolio website. Please take a look around!</p>
              </div>
              </>
            )}
              
          </div>
        </>
      )}
    </>
  );
};

export default Home;
