import React, { useEffect } from 'react';
import { useHistory } from 'react-router-dom';
import { useSpring, animated } from '@react-spring/web';

const AnimatedDiv = ({ children }) => {
  const history = useHistory();

  // Animation for appearing
  const appearAnimation = useSpring({
    from: { opacity: 0, translateY: 100 },
    to: { opacity: 1, translateY: 0 },
    config: { duration: 1000, easing: t => t * (2 - t) }, // Custom ease-out function
  });

  // Animation for disappearing when user navigates away
  const [disappearAnimation, setDisappearAnimation] = useSpring(() => ({
    to: { opacity: 0, translateY: 100 },
    config: { duration: 500 },
    onRest: () => {
      // Once the animation finishes, navigate to the new page
      history.push('/');
    },
  }));

  useEffect(() => {
    // Listen for beforeunload event to trigger the disappear animation
    const handleBeforeUnload = () => {
      setDisappearAnimation({ opacity: 0, translateY: 100 });
    };

    window.addEventListener('beforeunload', handleBeforeUnload);

    return () => {
      window.removeEventListener('beforeunload', handleBeforeUnload);
    };
  }, [setDisappearAnimation, history]);

  return (
    <animated.div
      style={{
        opacity: disappearAnimation.opacity,
        transform: disappearAnimation.translateY.interpolate((value) => `translateY(${value}px)`),
      }}
      className="padding"
    >
      <animated.div
        style={{
          opacity: appearAnimation.opacity,
          transform: appearAnimation.translateY.interpolate((value) => `translateY(${value}px)`),
        }}
        className="padding"
      >
        {children}
      </animated.div>
    </animated.div>
  );
};

export default AnimatedDiv;
