import React, { useState, useEffect } from 'react';
import {useSpring, animated} from '@react-spring/web';
import { useNavigate } from 'react-router-dom';


const AnimatedDiv = ({ children }) => {
    const [showComponent, setShowComponent] = useState(true);
    const history = useHistory();

    const { translateY } = useSpring({
      translateY: showComponent ? 0 : 100,
      config: { tension: 200, friction: 20 },
      onRest: () => {
        if (!showComponent) history.push('/'); // Redirect to home after animation ends
      },
    });

    useEffect(() => {
      setShowComponent(true); // Ensure the component is visible when it mounts
    }, []);

    useEffect(() => {
      return () => setShowComponent(false); // Animate out when unmounting
    }, []);

    return (
      <animated.div
        style={{
          transform: translateY.interpolate((value) => `translateY(${value}%)`),
          opacity: showComponent ? 1 : 0,
        }}
      >
        {children}
      </animated.div>
    );
  };

  export default AnimatedDiv;
