import React, { useState, useEffect } from 'react';
import { useSpring, animated, config } from 'react-spring';
import { useLocation } from 'react-router-dom';

const AnimatedDiv = ({ children }) => {
  const location = useLocation();
  const [showComponent, setShowComponent] = useState(true);

  useEffect(() => {
    setShowComponent(true); // Ensure the component is visible when it mounts
  }, [location.pathname]);

  const props = useSpring({
    opacity: showComponent ? 1 : 0,
    translateY: showComponent ? 0 : 100,
    config: config.default,
    onRest: () => {
      if (!showComponent) setShowComponent(false); // Hide the component after exit animation
    },
  });

  return (
    <animated.div
      style={{
        opacity: props.opacity,
        transform: props.translateY.interpolate((value) => `translateY(${value}px)`),
      }}
    >
      {children}
    </animated.div>
  );
};

export default AnimatedDiv;
